package operator;

public class AssignmentEx {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		//assignment operator
		
		
		int x=10;
		x+=10;
		System.out.println(x);
		
		x-=5;
		
		System.out.println(x);
		
		x*=10;
		System.out.println(x);
		
		x/=2;
		
		System.out.println(x);
		
		x%=5;
	}

}
