package operator;

public class LogicalOrEx {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		
		int a=10;
		int b=20;
		
		System.out.println(a>b  || ++a<b);
        //First condition is false it checks second condition
		 // ifFirst condition is true it  will not checks second condition
		//any condition is true it return true
		//System.out.println(a>b  &&  ++a<b);

		System.out.println(a);


		
		
		int x=10;
		int y=20;
		System.out.println(x>y  |  ++x<y);
       
		 // First condition is false  or true it  checks second condition
		//any condition is true  it return true
		//System.out.println(a>b  &&  ++a<b);

		System.out.println(x);


		
		
		
		
		

	}

}
