package operator;

public class PostAndPreIncrement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		int a=10;
		int b=20;
		
		System.out.println(a++ + b++); //20   +  30 
		System.out.println(a++  +b++);  //
		

		
		int x=10;
		int y=20;
		
		System.out.println(++x + y++); // 
		System.out.println(x++  + ++y);  //
		

		
		
		
		
		
	}

}
