package operator;

public class PreandPostIncrement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		int a=10;
		int b=++a;
		
		System.out.println(a); //20   +  30 
		System.out.println(b);  //
		

		
		int x=10;
		int y=x++;
		
		System.out.println(x); // 
		System.out.println(y);  //
		

		
		

	}

}
